async def test_template_vars_run_args(hass : HomeAssistant) -> None :
INDENT
    var = cv.SCRIPT_VARIABLES_SCHEMA(
        {
            "something" : "{{ run_var_ex + 1 }}",
            "something_2" : "{{ run_var_ex + 1 }}",
            })
    rendered = var.async_render(
        hass,
        {
            "run_var_ex" : 5,
            "something_2" : 1,
            },
        )
    assert rendered == {
        "run_var_ex" : 5,
        "something" : 6,
        "something_2" : 1,
        }
DEDENT

