    api.setTestCaseName('Installation_BasedOn_Menu_04_07')
    api.setTestCaseDescription('DVB-S Bisskey Control')
    if not api.start():
        try:
            if not api.DVBS:
                api.updateTestResult('N/A')
        except:
            api.printError()
        api.end(False)

